import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import axios from 'axios';

const style = {
    //Need to learn how to replace this with external style sheet.
    // I gave it a half hearted effort and it wasnt working
    margin: '10px',
    display: 'flex',
    justifyContent: 'flex-end',
}

class CreateTodo extends Component {
    // would like this to open a modal with a form
    // that will submit a new todo to the API

    state = {
        title: '',
        content: '',
        author: '',
    }

    render () {
        return (
            <div>
                <h1>Add a Todo</h1>
                <label>Title</label>
                <input type="text" value={this.state.title} onChange={(event) => this.setState({title:event.target.value})}/>
                <label>Content</label>
                <textarea value={this.state.content} cols="30" rows="4" onChange={(event) => this.setState({content:this.event.target.value})}></textarea>
                <Button style={style} variant="contained" color="Primary">Add</Button>
            </div>
        );
    }
}

export default CreateTodo;
